import { useState } from "react";
import { useMutation, useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import useForm from '../lib/useForm';
import styled from 'styled-components';
import { ALL_PRODUCTS_QUERY } from './Products';
import Form from './styles/Form';
import DisplayError from './ErrorMessage';
import Router from 'next/router';
import { StarsRating } from "stars-rating-react-hooks";

const CREATE_PRODUCT_REVIEW_MUTATION = gql`
    mutation CREATE_PRODUCT_REVIEW_MUTATION (
        $reviewTitle: String! 
        $comment: String!
        $stars: Int!
        $review: ProductReviewCreateInput!
    ){
      createProductReview ( 
        review: $review
        data: {
            reviewTitle: $reviewTitle
            comment: $comment
            stars: $stars             
        }) {
            id
            reviewTitle
            comment
            stars
        }
    }
`;

const StyledRatingWrapper = styled.div`
label .dv-star-rating .dv-star-rating-star {
    display: inline-block;
    padding: 5px;
    background: transparent;
    float: left;
}
`;

export default function createProductReview ({id}) {
    const { inputs, handleChange, clearForm, resetForm } = useForm({
        reviewTitle: '',
        comment: '',
        stars: '',
        review: id
    });

    const [createProductReview, { data, error, loading }] = useMutation(
        CREATE_PRODUCT_REVIEW_MUTATION, 
        {
            variables: inputs,
            refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
        }
    );

    const config = {
        totalStars: 5,
        initialSelectedValue: 1,
        renderFull: (
            <img
            alt="star"
            src="https://img.icons8.com/color/30/000000/filled-star--v1.png"
            />
        ),
        renderEmpty: (
            <img alt="star" src="https://img.icons8.com/windows/30/000000/filled-star.png" />
        ),
        renderHalf: (
            <img
            alt="star"
            src="https://img.icons8.com/color/30/000000/star-half-empty.png"
            />
        )
    };
    
    const [selecting, setSelecting] = useState(null);

    console.log(createProductReview);

    console.log(inputs.reviewTitle);

    return (
        <Form 
            onSubmit={async (e) => {
                e.preventDefault();
                //Submit the inputfield to the backend
                const res = await createProductReview();
                clearForm();
                // change them to the single item page;
                window.location.reload();
            }}
        >
            <DisplayError error={error} />
            <fieldset disabled={loading} aria-busy={loading}>
                <StyledRatingWrapper>
                    <label htmlFor="reviewTitle">
                        <input 
                            type="text" 
                            id="reviewTitle" 
                            name="reviewTitle" 
                            placeholder="Review Title"
                            required
                            value={inputs.reviewTitle}
                            onChange={handleChange}
                        />
                    </label>
                    <label htmlFor="comment">
                        <input 
                            type="text" 
                            id="comment" 
                            name="comment" 
                            placeholder="Review Comment"
                            required
                            value={inputs.comment}
                            onChange={handleChange}
                        />
                    </label>

                    {/* <h3>{selecting?.selectingValue}</h3> */}
                    <div>
                        <StarsRating
                        onStarsRated={(value) => {
                        }}
                        onSelecting={(isSelecting, selectingValue) => {
                            setSelecting({ isSelecting, selectingValue });
                        }}
                        config={config}
                        />
                    </div>

                    <label htmlFor="stars">
                        <input 
                            type="number" 
                            id="stars" 
                            name="stars" 
                            placeholder="Review Stars 1-5"
                            required
                            value={selecting?.selectingValue}
                            onChange={handleChange}
                        />
                    </label>
                    <button type="submit">Submit</button>
                    <button type="button" onClick={clearForm}>Clear Form</button>
                    <button type="button" onClick={resetForm}>Reset Form</button>
                </StyledRatingWrapper>
            </fieldset>
        </Form>
    );
}
