import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import gql from "graphql-tag";
import { useQuery } from '@apollo/client';
import DisplayError from "./ErrorMessage";
import { useUser } from './User';
import Product from "./Product";

const ItemsList = styled.div`
	display: grid;
	grid-template-columns: auto auto auto auto;
	grid-gap: 60px;
	max-width: ${props => props.theme.maxWidth};
	margin: 0 auto;
	@media (max-width: 830px) {
		grid-template-columns: 1fr 1fr;
		grid-gap: 0%;
		a {
    		font-size: 1.2rem;
		}
	}
	@media (max-width: 500px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;
	}
`;

const SOME_PRODUCTS_QUERY = gql`
    query SOME_PRODUCTS_QUERY ($id: ID!, $skip: Int = 0, $first: Int = 20){
        allProducts (where: {name_contains_i: "Yeti"}, first: $first, skip: $skip, sortBy: stars_DESC) {
            name
            price
            author
            id
            photo {
                altText
                image {
                    publicUrlTransformed
                }
            }
        }
    }
`;

export default function SomeProduct({ id }) {
    const user = useUser();

    const { data, loading, error } = useQuery
        (SOME_PRODUCTS_QUERY, {
            variables: {
                id,
            },
        });

    if (loading) return <p>Loading...</p>;
    if (error) return <DisplayError error={error} />;
    
    const { allProducts } = data;

    console.log(allProducts)
    
    return (

        <div>
            <ItemsList>
                {data.allProducts.map(product => (
                    <div key={product.id}>
                        <Product product={product} key={product.id} />
                    </div>
                ))}
            </ItemsList>
        </div>
    );
}
