import styled from 'styled-components';
import gql from "graphql-tag";
import { useQuery } from '@apollo/client';
import DisplayError from "./ErrorMessage";
import Head from 'next/head';
import SingleItemStyles from './styles/SingleItemStyles';
import PriceTag from './styles/PriceTag';
import formatMoney from '../lib/formatMoney';
import ReactImageMagnify from 'react-image-magnify';
import SliderImage from 'react-zoom-slider';
import { useUser } from './User';
import AddToCart from './AddToCart';
import ItemStyles from './styles/ItemStyles';
import CreateRating from './CreateRating';
import useInfiniteScroll from 'react-infinite-scroll-hook';

const Center = styled.div`
	text-align: center;
`;

const SINGLE_ITEM_QUERY = gql`
    query SINGLE_ITEM_QUERY ($id: ID!){
        Product(where: { id: $id }) {
            name
            price
            description
            inStock
            ageRange
            author
            id
            photo {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo2 {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo3 {
                altText
                image {
                    publicUrlTransformed
                }
            }

            photo4 {
                altText
                image {
                    publicUrlTransformed
                }
            }

            review {
                id
                reviewTitle
                comment
                stars
            }
            
        }
        
        # allProducts (where: {
        #   name_contains_i: "Yeti"
        # }) {
        #   name
        #   price
        #   description
        # }
    }

`;

export default function SingleProduct({ id }) {
    const { loadingReview, items, hasNextPage, errorReview, loadMore } = useLoadItems();
    const [sentryRef] = useInfiniteScroll({
        loadingReview,
        hasNextPage,
        onLoadMore: loadMore,
        // When there is an error, we stop infinite loading.
        // It can be reactivated by setting "error" state as undefined.
        disabled: !!errorReview,
        // `rootMargin` is passed to `IntersectionObserver`.
        // We can use it to trigger 'onLoadMore' when the sentry comes near to become
        // visible, instead of becoming fully visible on the screen.
        rootMargin: '0px 0px 400px 0px',
    });

    const user = useUser();

    const { data, loading, error } = useQuery
        (SINGLE_ITEM_QUERY, {
            variables: {
                id,
            },
        });
    if (loading) return <p>Loading...</p>;
    if (error) return <DisplayError error={error} />; 
    const { Product } = data;
    console.log(Product)

    const dataImage = [
        {
            image: Product?.photo?.image?.publicUrlTransformed,
            text: Product?.photo?.altText
        },
        {
            image: Product?.photo2?.image?.publicUrlTransformed,
            text: Product?.photo2?.altText
        },
        {
            image: Product?.photo3?.image?.publicUrlTransformed,
            text: Product?.photo3?.altText
        },
        {
            image: Product?.photo4?.image?.publicUrlTransformed,
            text: Product?.photo4?.altText
        },
    ];
    
    return (
        <SingleItemStyles>
            <Head>
                <title>Stax Of Stories - {Product.name}</title>
            </Head>
            <div className="item-details">

                <SliderImage 
                    data={dataImage} 
                    width="500px" 
                    showDescription={false} 
                    direction="right" 
                />

                <div className="details">
                    <h2>{Product.name}</h2>
                    <p>{Product.description}</p>
                    <PriceTag>{formatMoney(Product.price)}</PriceTag>
                    <ItemStyles>
                        <p>{Product?.ageRange} years</p>
                        <p>By {Product?.author}</p>
                        {
                            Product.inStock < 6 && Product.inStock > 0 ? <p className="font-weight-bold" style={{color: Product.inStock < 6 && Product.inStock > 0 ? "red" : "#8dc63f"}}>Low stock</p> : '' 
                        }
                        {
                            Product.inStock < 1 ? <p className="font-weight-bold" style={{color: Product.inStock < 1 ? "red" : "#8dc63f"}}>Back in stock soon!</p> : '' 
                        }

                        {!user && ( 
                            <>
                                <div className="buttonList">
                                    <a href="/signup" prefetch="true"><button className="cart-btn">Sign In/Buy</button></a>
                                </div>
                            </>
                        )}

                        {user?.role === null && ( 
                            <>
                                {
                                    Product?.inStock > 1 ? 
                                    <div className="buttonList">
                                        <AddToCart id={Product.id} />
                                    </div> : '' 
                                }
                            </>
                        )}

                        {user?.role?.canManageProducts === true && ( 
                            <>
                                {
                                    Product?.inStock > 1 ? 
                                    <div className="buttonList">
                                        <AddToCart id={Product.id} />
                                    </div> : '' 
                                }
                                <div className="buttonList">
                                    <Link href={{
                                        pathname: '/update',
                                        query: {
                                            id: Product.id,
                                        },
                                    }}>
                                    <a><button className="btn btn-success">Edit</button></a>
                                    </Link>
                                    <a><DeleteProduct id={Product.id}>Delete Product</DeleteProduct></a>
                                </div>
                            </>
                        )}
                    </ItemStyles>
                </div>
            </div>

            <h2>Write a review</h2>
            <Center>
                <CreateRating product={Product} />
                    <br /> 
                    
                    {Product.review.length < 1 && (
                        <p>Be the first to write a review</p>
                    )}

                    {Product.review.length > 0 && (
                        <ItemRatingsList>

                            <List>
                                {Product.review.map((review) => (
                                    <ListItem key={review.key}>{review.value}</ListItem>
                                ))}
                                {/* 
                                    As long as we have a "next page", we show "Loading" right under the list.
                                    When it becomes visible on the screen, or it comes near, it triggers 'onLoadMore'.
                                    This is our "sentry".
                                    We can also use another "sentry" which is separated from the "Loading" component like:
                                        <div ref={sentryRef} />
                                        {loading && <ListItem>Loading...</ListItem>}
                                    and leave "Loading" without this ref.
                                */}
                                {(loadingReview || hasNextPage) && (
                                    <ListItem ref={sentryRef}>
                                    <Loading />
                                    </ListItem>
                                )}
                            </List>

                            {/* <InfiniteScroll
                                dataLength={this.state.ratings.length}
                                next={this.fetchMoreData}
                                hasMore={this.state.hasMore}
                                loader={<h5></h5>}
                                height={500}
                                endMessage={
                                <p style={{ textAlign: "center" }}>
                                    <b>You have seen all the reviews on this item</b>
                                </p>
                                }
                            >

                                {Product.review.map(review => (
                                <div>
                                    <Reviews review={review} key={review.id} />
                                    <ReviewStyles><p>{formatDistance(rating.createdAt, new Date())} ago</p></ReviewStyles>
                                </div>
                                ))}
                            
                            </InfiniteScroll> */}
                        </ItemRatingsList>
                    )}
            </Center>
        </SingleItemStyles>
    );
}
