import React, { useRef, useState } from 'react';
import Form from './styles/Form';
import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';
import styled from 'styled-components';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Router from 'next/router'; 
import useForm from '../lib/useForm';
import Error from './ErrorMessage';
import { 
    faEye,
} from '@fortawesome/free-solid-svg-icons';
import { CURRENT_USER_QUERY } from './User';
import emailjs from 'emailjs-com'; 

const EyeReveal = styled.div`
  .cursor-pointer {
    cursor: pointer;
  }

  .show{
  	opacity: 1;
  }

  .hide {
  	opacity: 0.5;
  }
`;

const SIGNUP_MUTATION = gql`
  mutation SIGNUP_MUTATION($email: String!, $name: String!, $password: String!){
    createUser(data: { email: $email name: $name password: $password
    }) {
        id
        email
        name
    }
  }
`;

export default function SignUp() {

    const form = useRef();
    const [status, setStatus] = useState(undefined);
    const [shown, setShown] = useState(false);

    const { inputs, handleChange, resetForm } = useForm({
        email: '',
        name: '',
        password: '',
    });

    const [signup, { data, loading , error}] = useMutation(SIGNUP_MUTATION, 
        {
            variables: inputs,
            // refetchQueries: [{ query: CURRENT_USER_QUERY }],
        }
    );

    async function handleSubmit(e) {
        e.preventDefault();
        // console.log(inputs);
        const res = await signup().catch(console.error);
        emailjs.sendForm('service_ipz8izo', 'welcome_ff050t8', form.current, 'user_uecjPyjaRPSXr3Lncpkcz')
        .then((result) => {
            console.log(result.text);
            setStatus({type: 'result'});
        }, (error) => {
            console.log(error.text);
            setStatus({type: 'error'})
        });
        // console.log(res);
        // console.log({ data, loading, error });
        resetForm();
    }

    return (
        <Form ref={form} method="POST" onSubmit={handleSubmit}>
            <h2>Sign Up For An Account</h2>
            <fieldset>
                {data?.createUser && (
                    <p>Signed up with {data.createUser.email} - Please Go Ahead and Sign in!</p>
                )}
                <Error error={error} />
                <label htmlFor="name">
                    Name
                    <input 
                        type="name" 
                        name="name" 
                        placeholder="Your Name" 
                        autoComplete="name"
                        value={inputs.name || ''}
                        onChange={handleChange}
                    />
                </label>

                <label htmlFor="email">
                    Email
                    <input 
                        type="email" 
                        name="email" 
                        placeholder="Your Email Address" 
                        autoComplete="email"
                        value={inputs.email || ''}
                        onChange={handleChange}
                    />
                </label>
                <EyeReveal>
                    <label htmlFor="password">
                        Password
                            
                            <div onClick={() => setShown(!shown)} className="float-right mt-1 cursor-pointer">
                                <small>
                                    <span className="pr-1">Show Password </span>
                                    <FontAwesomeIcon icon={faEye} />
                                </small>
                            </div>
                                                            
                        <input 
                            type={shown ? "text" : "password"}
                            name="password" 
                            placeholder="Password" 
                            autoComplete="password"
                            value={inputs.password || ''}
                            onChange={handleChange}
                        />
                        
                    </label>
                </EyeReveal>
                <button type="submit">Sign Up!</button>
                <div className="float-right mt-1">
                    <small>
                        <a className="member-link" href="/signin"><span className="cursor-pointer">Sign In</span></a> or <a className="member-link" href="/requestReset"><span className="pr-1 cursor-pointer">Reset Password</span></a>
                    </small>
                </div>
            </fieldset>
        </Form>
    );
}
