import Form from './styles/Form';
import DisplayError from './ErrorMessage';
import Router from 'next/router';
import { useState } from 'react';

const SINGLE_PRODUCT_QUERY = gql`
    query SINGLE_PRODUCT_QUERY($id: ID!) {
        Product(where: { id: $id }) {
            id
            name
            description
            inStock
            ageRange
            author
            price   
            photo {
                id
                image {
                publicUrlTransformed
                }
            }
            photo2 {
                id
                image {
                publicUrlTransformed
                }
            }
            photo3 {
                id
                image {
                publicUrlTransformed
                }
            }
            photo4 {
                id
                image {
                publicUrlTransformed
                }
            }
        }
    }
`;

const UPDATE_PRODUCT_MUTATION = gql`
    mutation UPDATE_PRODUCT_MUTATION (
        $id: ID!
        $name: String!
        $ageRange: String!
        $inStock: String!
        $author: String!
        $price: Int!
        $image: Upload
        $image2: Upload
        $image3: Upload
        $image4: Upload
        $description: String!
    ){
        updateProduct(
            id: $id
            data: {
                name: $name
                ageRange: $ageRange
                inStock: $inStock
                author: $author
                price: $price
                description: $description
                status: "AVAILABLE"
                photo: { create: { image: $image, altText: $name } }
                photo2: { create: { image: $image2, altText: $name } }
                photo3: { create: { image: $image3, altText: $name } }
                photo4: { create: { image: $image4, altText: $name } }
            }
        ) {
            id
            name
            description
            inStock
            ageRange
            author
            price
            photo {
                id
                image {
                publicUrlTransformed
                }
            }
            photo2 {
                id
                image {
                publicUrlTransformed
                }
            }
            photo3 {
                id
                image {
                publicUrlTransformed
                }
            }
            photo4 {
                id
                image {
                publicUrlTransformed
                }
            }
        }
    }
`;

const StyledUploadFileBtn = styled.div`
label{
  padding:5px 10px;
  background:#efefef;
  position:relative;
  color:#606060;
  border-radius:2px;
  text-align:center;
  cursor:pointer
}

.upload-btn-file {
    position: absolute;
    z-index: 1000;
    opacity: 0;
    cursor: pointer;
    background:#efefef;
    color:#606060;
    right: 0;
    top: 0;
    height: 100%;
    font-size: 24px;
    width: 100%;
    cursor:pointer
}
`;

export default function UpdateProduct ({ id }) {

    const { loading, error, data } = useQuery
    (SINGLE_PRODUCT_QUERY, {
        variables: { id }
    });

    const [updateProduct, { data: updateData, error: updateError, loading: updateLoading }] = useMutation
    (UPDATE_PRODUCT_MUTATION);

    const { inputs, handleChange, clearForm, resetForm } = useForm(data?.Product);

    console.log(inputs);
    console.log(handleChange);

    const [image, setImage ] = useState("");
    const [ url, setUrl ] = useState("");

    const uploadImage = () => {
    const data = new FormData()
    data.append("file", image)
    data.append("upload_preset", "staxxxx")

    fetch("https://api.cloudinary.com/v1_1/dcnxv4ksy/image/upload",{
        method:"post",
        body: data
    })
    .then(resp => resp.json())
    .then(data => {
        setUrl(data.url)
    })
        .catch(err => console.log(err))
    }

    // // const uploadFile = async e => {
	// // 	console.log('uploading file...');

	// // 	const files = e.target.files;
	// // 	const data = new FormData();
	// // 	data.append('file', files[0]);
	// // 	data.append('upload_preset', 'staxxxx');

	// // 	const res = await fetch('https://api.cloudinary.com/v1_1/dcnxv4ksy/image/upload', {
	// // 		method: 'POST',
	// // 		body: data,
	// // 	});
	// // 	const file = await res.json();
		
	// // 	console.log(file.secure_url);
    // //     console.log(files);
	// // 	setImageUpload(
    // //         file.secure_url
    // //     );
	// // };

    // console.log(imageUpload);

    if (loading) return <p>loading...</p>;

    return (
        <Form 
            onSubmit={async (e) => {
                e.preventDefault();
                const res = await updateProduct({
                    variables: {
                        id,
                        name: inputs.name,
                        description: inputs.description,
                        price: inputs.price,    
                        inStock: inputs.inStock,
                        ageRange: inputs.ageRange,
                        author: inputs.author,
                        image: inputs.image, 
                        image2: inputs.image2,      
                        image3: inputs.image3,  
                        image4: inputs.image4,                      
                    },
                }).catch(console.error);
                console.log(res);
                //Submit the inputfield to the backend
                // const res = await createProduct();
                // clearForm();
                Router.push({ pathname: `/`})
                //     pathname: `/product/${res.data.createProduct.id}`,
                // });


            }}
        >
            <DisplayError error={error || updateError} />
            <fieldset disabled={updateLoading} aria-busy={updateLoading}>
                <div>
                    <input type="file" onChange= {(e)=> setImage(e.target.files[0])}></input>
                    <button onClick={uploadImage}>Upload</button>
                </div>
                <div>
                    <h1>Uploaded image will be displayed here</h1>
                    <img src={url}/>
                </div>
                <StyledUploadFileBtn>
                    <label htmlFor="image">
                        {inputs?.photo?.image?.publicUrlTransformed && (
