import { useState } from 'react';
import styled from 'styled-components';
import {
    CardElement,
    Elements,
    useElements,
    useStripe,
  } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
    faChevronRight,
} from '@fortawesome/free-solid-svg-icons';
import nProgress from 'nprogress';
import gql from 'graphql-tag';
import { useMutation } from '@apollo/client';
import { CURRENT_USER_QUERY } from './User';
import { useRouter } from 'next/dist/client/router';
import { useCart } from '../lib/cartState';

const CheckoutFormStyles = styled.form`
    box-shadow: 0 1px 2px 2px rgba(0, 0, 0, 0.04);
    border: 1px solid rgba(0, 0, 0, 0.06);
    border-radius: 5px;
    padding: 1rem;
    display: grid;
    grid-gap: 1rem;
`;

const Overlay = styled.div `
  &:before {
    content: "";
    display: ${props => props.toggleOverlay ? 'block': 'none'};
    background: rgba(0, 0, 0, 0.6);
    position: fixed;
    top: 0;
    left: -860px;
    right: 0;
    bottom: 0;
    z-index: 1031;
  }
`;

const CREATE_ORDER_MUTATION = gql`
    mutation CREATE_ORDER_MUTATION ($token: String!) {
        checkout(token: $token) {
            id
            charge
            total
            items {
                id
                name
            }
        }
    }
`;

const stripeLib = loadStripe(process.env.NEXT_PUBLIC_STRIPE_KEY);

function CheckoutForm() {
    const [error, setError] = useState();
    const [loading, setLoading] = useState(false);
    const stripe = useStripe();
    const router = useRouter();
    const elements = useElements();
    const { closeCart } = useCart();
    const [checkout, {error: graphQLError}] = useMutation(
        CREATE_ORDER_MUTATION,
        {
            refetchQueries: [{ query: CURRENT_USER_QUERY }],
        }
    );
    async function handleSubmit(e) {
        //1. Stop the form from submitting and turn the loader one
        e.preventDefault();
        setLoading(true);
        console.log('We gotta do some work...');
        //2. Start the page transition
        nProgress.start();
        //3. Create the payment method via stripe
        const { error, paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card: elements.getElement(CardElement),
        });
        console.log(paymentMethod)
        //4. Handle any errors from stripe (Token comes back here if successful)
        if (error) {
            setError(error);
            nProgress.done();
            return; //stops the checkout from happening
        }
        //5. Send the token from step 3 to our keystone server, via a custom mutation!
        const order = await checkout({
            variables: {
                token: paymentMethod.id
            },
        });
        console.log(`Finished with the order!!`);
        console.log(order);
        closeCart()

        //6. Change the page to vie the order
        router.push({
            pathname: `/order/[id]`,
            query: { id: order.data.checkout.id },
        });
        //7. Close the cart 
        closeCart()

        //8. Turn the loader off
        setLoading(false);
        nProgress.done();
    }

    return (
        <>
        <Overlay toggleOverlay={toggle} onClick={handleToggle}>
        </Overlay>
        <CheckoutFormStyles onSubmit={handleSubmit}>
            {error && <p style={{ fontSize: 12 }}>{error.message}</p>}
            {graphQLError && <p style={{ fontSize: 12 }}>{graphQLError.message}</p>}
            <CardElement />
            <button id="buyIt" className="btn btn-success checkout" type="submit">Buy Now <FontAwesomeIcon icon={faChevronRight} /></button>
        </CheckoutFormStyles>
        </>
    );
}

function Checkout() {
    return (
        <Elements stripe={stripeLib}>
            <CheckoutForm />
        </Elements>
    )
}

export {Checkout};
