// import gql from "graphql-tag";
import { gql, useQuery } from "@apollo/client";

export const CURRENT_USER_QUERY = gql`
    query {
        authenticatedItem {
            ... on User {
                id
                email
                name
                cart {
                    id
                    quantity
                    product {
                        id
                        price
                        name
                        description
                        photo {
                            image {
                                publicUrlTransformed
                            }
                        }
                    }
                }
            }
        }
    }
`;


export const CURRENT_ROLE_QUERY = gql`
    query {
        authenticatedItem {
            ... on User {
                id
                email
                name
                cart {
                    id
                    quantity
                    product {
                        id
                        price
                        name
                        description
                        photo {
                            image {
                                publicUrlTransformed
                            }
                        }
                    }
                }
            }
        }
    }
`;

export function useUser() {
    const { data } = useQuery(CURRENT_USER_QUERY);
    return data?.authenticatedItem;
}

export function useRole() {
    const { data } = useQuery(CURRENT_ROLE_QUERY);
    return data?.authenticatedItem;
}
