import { useState } from "react";
import { useMutation, useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import useForm from '../lib/useForm';
import styled from 'styled-components';
import { ALL_PRODUCTS_QUERY } from './Products';
import Form from './styles/Form';
import DisplayError from './ErrorMessage';
import Router from 'next/router';
import { StarsRating } from "stars-rating-react-hooks";

const SHIPPING_QUERY = gql`
    query SHIPPING_QUERY {
        allShippings {
            id
            type
            amount
            description
        }
    }
`;

const CREATE_ORDER_MUTATION = gql`
    mutation CREATE_ORDER_MUTATION ($shipping: ShippingRelateToOneInput!) {
        checkoutToShipping(
            data: {
                shipping: $shipping
            }
        ) {
            id
            shipping {
                id
                type
                amount
                description
            }
        }
    }
`;

export default function createShippingMethod ({order}) {

    const { inputs, handleChange } = useForm({
        shipping: {
            type: '',
            amount: '',
            connect: { id: order.id }
        },
    });
    
    async function onChangeValue(e) {

        setShippingMethod(e.target.value);

        const order = await checkout({
            variables: {
                token: paymentMethod.id,
                shipping: {
                    type: inputs.type,
                    amount: inputs.amount,
                    connect: { id: order.id }
                },  
            },
        });
    }

    const [createShippingMethod, { data, error, loading }] = useMutation(
        CREATE_PRODUCT_REVIEW_MUTATION, 
        {
            variables: inputs,
            refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
        }
    );
    
    return (
        <select
            name="transcode_profile"
            id="transcode_profile"
            onChange={onChangeValue}
            value={shippingMethod.type}
            >
                <option value="Select Shipping Method">Select Shipping Method</option>
                {data?.allShippings.map(shippingMethod => <option value={shippingMethod.id}>{shippingMethod.type} - {shippingMethod.amount}</option>)}
        </select>
    );
}
