import React, { Component } from 'react';
import styled from 'styled-components';
import { Mutation, Query } from 'react-apollo';
import gql from 'graphql-tag';
import Router from 'next/router'; 
import Form from './styles/Form';
import ItemStyles from './styles/ItemStyles';
import formatMoney from '../lib/formatMoney';
import SubHeader from '../components/SubHeader';
import Error from './ErrorMessage';

const StyledUploadFileBtn = styled.div`
label{
  padding:5px 10px;
  background:#efefef;
  position:relative;
  color:#606060;
  border-radius:2px;
  text-align:center;
  cursor:pointer
}

.upload-btn-file {
    position: absolute;
    z-index: 1000;
    opacity: 0;
    cursor: pointer;
    background:#efefef;
    color:#606060;
    right: 0;
    top: 0;
    height: 100%;
    font-size: 24px;
    width: 100%;
    cursor:pointer
}
`;

const SINGLE_ITEM_QUERY = gql`
	query SINGLE_ITEM_QUERY($id: ID!){
		item(where: { id: $id }){
			id
			title
			description
			price
			author
			image
			image2
			image3
			image4
			largeImage
			largeImage2
			largeImage3
			largeImage4
			ageRange
			inStock
		}
	}
`;

const UPDATE_ITEM_MUTATION = gql`
	mutation UPDATE_ITEM_MUTATION (
		$id: ID!,
		$title: String,
		$description: String,
		$price: Int,
		$inStock: String,
		$image: String,
		$image2: String,
		$image3: String,
		$image4: String,
		$largeImage: String,
		$largeImage2: String,
		$largeImage3: String,
		$largeImage4: String,
		$author: String,
		$ageRange: String
	){
		updateItem(
			id: $id,
			title: $title,
			description: $description,
			price: $price,
			inStock: $inStock,
			image: $image,
			image2: $image2,
			image3: $image3,
			image4: $image4,
			largeImage: $largeImage,
			largeImage2: $largeImage2,
			largeImage3: $largeImage3,
			largeImage4: $largeImage4,
			author: $author,
			ageRange: $ageRange

		) {
			id
			title
			description
			price
			inStock
			image
			image2
			image3
			image4
			largeImage
			largeImage2
			largeImage3
			largeImage4
			author
			ageRange
		}
	}
`;

class UpdateItem extends Component {
	state = {};

	handleChange = e => {
		const { name, type, value } = e.target;
		const val = type === 'number' ? parseFloat(value) : value;
		this.setState({ [name]: val });
	};
	updateItem = async (e, updateItemMutation) => {
		e.preventDefault();
		console.log('Updating Item!!');
