import Form from './styles/Form';
import { useMutation } from '@apollo/client';
import gql from 'graphql-tag';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import useForm from '../lib/useForm';
import Error from './ErrorMessage';
import { 
    faEye,
} from '@fortawesome/free-solid-svg-icons';

const CONTACT_REQUEST_MUTATION = gql `
	mutation CONTACT_REQUEST_MUTATION(
		$email: String!
		$subject: String! 
		$message: String!	
	) {
		createContact(
			email: $email 
			subject: $subject
			message: $message
		) {
			message
		}
	}
`;

export default function ContactRequest() {

    const { inputs, handleChange, resetForm } = useForm({
        email: '',
        name: '',
        password: '',
    });

    const [createContact, { data, loading , error}] = useMutation(CONTACT_REQUEST_MUTATION, 
        {
            variables: inputs,
            // refetchQueries: [{ query: CURRENT_USER_QUERY }],
        }
    );

    async function handleSubmit(e) {
        e.preventDefault();
        // console.log(inputs);
        const res = await createContact().catch(console.error);
        // console.log(res);
        // console.log({ data, loading, error });
        resetForm();
    }

    // if (
    //     data?.authenticateUserWithPassword.__typename ===
    //     'UserAuthenticationWithPasswordSuccess'
    // ) {
    //     Router.push({
    //         pathname: '/',
    //     });

    // }
    
    // const error = data?.authenticateUserWithPassword.__typename ===
    // 'UserAuthenticationWithPasswordFailure'
    //   ? data?.authenticateUserWithPassword
    //   : undefined;

    return (
        <Form method="POST" onSubmit={handleSubmit}>
            <h2>Sign Up For An Account</h2>
            <fieldset>
				<h2>Contact Us</h2>
				<br />
				<h4>Leave a message for us right here and we’ll get back to you as soon as we can!</h4>
				<br />
                <Error error={error} />
                <label htmlFor="name">
                    Name
                    <input 
                        type="name" 
                        name="name" 
                        placeholder="Your Name" 
                        autoComplete="name"
                        value={inputs.name}
                        onChange={handleChange}
                    />
                </label>

                <label htmlFor="email">
                    Email
                    <input 
                        type="email" 
                        name="email" 
                        placeholder="Your Email Address" 
                        autoComplete="email"
                        value={inputs.email}
                        onChange={handleChange}
                    />
                </label>
				<label htmlFor="password">
					Password
						
						<div className="float-right mt-1 cursor-pointer">
							<small>
								<span className="pr-1">Show Password </span>
								<FontAwesomeIcon icon={faEye} />
							</small>
						</div>
														
					<input 
						type="password"
						name="password" 
						placeholder="Password" 
						autoComplete="password"
						value={inputs.password}
						onChange={handleChange}
					/>
					
				</label>
                <button type="submit">Sign Up!</button>
                <div className="float-right mt-1">
                    <small>
                        <a className="member-link" href="/signin"><span className="cursor-pointer">Sign In</span></a> or <a className="member-link" href="/requestReset"><span className="pr-1 cursor-pointer">Reset Password</span></a>
                    </small>
                </div>
            </fieldset>
        </Form>
    );
}
