import React, { useRef, useState } from 'react';
import styled from 'styled-components';
import {
    CardElement,
    Elements,
    useElements,
    useStripe,
  } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
    faChevronRight,
} from '@fortawesome/free-solid-svg-icons';
import nProgress from 'nprogress';
import gql from 'graphql-tag';
import { useMutation } from '@apollo/client';
import { CURRENT_USER_QUERY, useUser } from './User';
import { useRouter } from 'next/dist/client/router';
import { useCart } from '../lib/cartState';
import useForm from '../lib/useForm';
import emailjs from 'emailjs-com';


const CheckoutFormStyles = styled.form`
    box-shadow: 0 1px 2px 2px rgba(0, 0, 0, 0.04);
    border: 1px solid rgba(0, 0, 0, 0.06);
    border-radius: 5px;
    padding: 1rem;
    display: grid;
    grid-gap: 1rem;
`;

const CREATE_ORDER_MUTATION = gql`
    mutation CREATE_ORDER_MUTATION ($token: String!) {
        checkout(token: $token) {
            id
            charge
            total
            items {
                id
                name
            }
        }
    }
`;

const stripeLib = loadStripe(process.env.NEXT_PUBLIC_STRIPE_KEY);

function CheckoutForm() {

    const form = useRef();
    const [status, setStatus] = useState(undefined);

    // const { inputs, handleChange } = useForm({
    //     email: '',
    //     name: '',
    // });

    const [error, setError] = useState();
    const [loading, setLoading] = useState(false);
    const stripe = useStripe();
    const router = useRouter();
    const elements = useElements();
    const [billingDetails, setBillingDetails] = useState({
        email: "",
        phone: "",
        name: ""
    });
    const { closeCart } = useCart();
    const [checkout, {error: graphQLError}] = useMutation(
        CREATE_ORDER_MUTATION,
        {
            refetchQueries: [{ query: CURRENT_USER_QUERY }],
        }
    );

    const user = useUser();
	// console.log(user);

    async function handleSubmit(e) {
        //1. Stop the form from submitting and turn the loader one
        e.preventDefault();

        emailjs.sendForm('service_ipz8izo', 'order_6vr15fv', form.current, 'user_uecjPyjaRPSXr3Lncpkcz')
        .then((result) => {
            console.log(result.text);
            setStatus({type: 'result'});
        }, (error) => {
            console.log(error.text);
            setStatus({type: 'error'})
        });

        setLoading(true);
        console.log('We gotta do some work...');
        //2. Start the page transition
        nProgress.start();
        //3. Create the payment method via stripe
        const { error, paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card: elements.getElement(CardElement),
        });
        console.log(paymentMethod)
        //4. Handle any errors from stripe (Token comes back here if successful)
        if (error) {
            setError(error);
            nProgress.done();
            return; //stops the checkout from happening
        }
        //5. Send the token from step 3 to our keystone server, via a custom mutation!
        const order = await checkout({
            variables: {
                token: paymentMethod.id
            },
        });
        console.log(`Finished with the order!!`);
        console.log(order);
        //6. Change the page to vie the order
        router.push({
            pathname: `/order/[id]`,
            query: { id: order.data.checkout.id },
        });
        //7. Close the cart 
        closeCart()

        //8. Turn the loader off
        setLoading(false);
        nProgress.done();
    }

    return (
        <CheckoutFormStyles ref={form} onSubmit={handleSubmit}>
            <input 
                type="hidden" 
                name="email" 
                placeholder="Your Email Address" 
                value={user.email}
            />
            <fieldset className="FormGroup">
                <Field
                label="Name"
                id="name"
                type="text"
                placeholder="Jane Doe"
                required
                autoComplete="name"
                value={billingDetails.name}
                onChange={(e) => {
                    setBillingDetails({ ...billingDetails, name: e.target.value });
                }}
                />
                <Field
                label="Email"
                id="email"
                type="email"
                placeholder="janedoe@gmail.com"
                required
                autoComplete="email"
                value={billingDetails.email}
                onChange={(e) => {
                    setBillingDetails({ ...billingDetails, email: e.target.value });
                }}
                />
                <Field
                label="Phone"
                id="phone"
                type="tel"
                placeholder="(941) 555-0123"
                required
                autoComplete="tel"
                value={billingDetails.phone}
                onChange={(e) => {
                    setBillingDetails({ ...billingDetails, phone: e.target.value });
                }}
                />
            </fieldset>
            {error && <p style={{ fontSize: 12 }}>{error.message}</p>}
            {graphQLError && <p style={{ fontSize: 12 }}>{graphQLError.message}</p>}
            <CardElement />
            <button id="buyIt" className="btn btn-success checkout" type="submit">Buy Now <FontAwesomeIcon icon={faChevronRight} /></button>
        </CheckoutFormStyles>
    );
}

function Checkout() {
    return (
        <Elements stripe={stripeLib}>
            <CheckoutForm />
        </Elements>
    )
}

export {Checkout};
