import React, { useState, useEffect } from "react";
import styled from 'styled-components';
import gql from "graphql-tag";
import { useQuery } from '@apollo/client';
import DisplayError from "./ErrorMessage";
import Head from 'next/head';
import Link from 'next/link';
import SingleItemStyles from './styles/SingleItemStyles';
import DeleteProduct from './DeleteProduct';
import PriceTag from './styles/PriceTag';
import formatMoney from '../lib/formatMoney';
import SliderImage from 'react-zoom-slider';
import { useUser } from './User';
import AddToCart from './AddToCart';
import ItemStyles from './styles/ItemStyles';
import CreateRating from './CreateRating';
import Reviews from './Reviews';

const Center = styled.div`
	text-align: center;
`;

const ItemsList = styled.div`
	display: grid;
	grid-template-columns: auto auto auto auto;
	grid-gap: 60px;
	max-width: ${props => props.theme.maxWidth};
	margin: 0 auto;
	@media (max-width: 830px) {
		grid-template-columns: 1fr 1fr;
		grid-gap: 0%;
		a {
    		font-size: 1.2rem;
		}
	}
	@media (max-width: 500px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;
	}
`;

const ItemRatingsList = styled.div`
	display: grid;
	grid-template-columns: 1fr;
	grid-gap: 60px;
	max-width: ${props => props.theme.maxWidth};
	margin: 0 auto;
	text-align: left;

	p{
		font-size: 1rem;
	}

	@media (max-width: 830px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;

		p {
    		font-size: 0.95rem;
		}
		
		a {
    		font-size: 1.2rem;
		}
	}
	@media (max-width: 500px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;
	}
`;


const SINGLE_ITEM_QUERY = gql`
    query SINGLE_ITEM_QUERY ($id: ID!, $skip: Int = 0, $first: Int){
        Product(where: { id: $id }) {
            name
            price
            description
            inStock
            ageRange
            author
            id
            photo {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo2 {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo3 {
                altText
                image {
                    publicUrlTransformed
                }
            }

            photo4 {
                altText
                image {
                    publicUrlTransformed
                }
            }

            review (first: $first, skip: $skip, sortBy: stars_DESC) {
                id
                reviewTitle
                comment
                stars
            }
            
        }
        
        # allProducts (where: {
        #   name_contains_i: "Yeti"
        # }) {
        #   name
        #   price
        #   description
        # }
    }

`;

export default function SingleProduct({ id }) {

    const [reviewData, setReviewData] = useState(data);
    const [listItems, setListItems] = useState(
        Array.from(Array(5).keys(), n => n + 1)
    );

    console.log(reviewData);
    
    const [isFetching, setIsFetching] = useState(false);

    useEffect(() => {
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);
    // we use an empty array [] as the second parameter of useEffect, that tells the useEffect function to act like componentDidMount and only run onee time, when the component first mounts.

    useEffect(() => {
        if (!isFetching) return;
        fetchMoreListItems();
    }, [isFetching]);

    const handleScroll = () => {
    if (
        window.innerHeight + document.documentElement.scrollTop !==
        document.documentElement.offsetHeight
    )
        return;
        setIsFetching(true);
    };

    const fetchMoreListItems = () => {
        setTimeout(() => {
            setListItems(prevState => [
            ...prevState,
            ...Array.from(Array(10).keys(), n => n + prevState.length + 1)
            ]);
            setIsFetching(false);
        }, 2000);
    };

    const user = useUser();

    const { data, loading, error } = useQuery
        (SINGLE_ITEM_QUERY, {
            variables: {
                id,
            },
        });
    if (loading) return <p>Loading...</p>;
    if (error) return <DisplayError error={error} />;

    const { Product } = data;

    const dataImage = [
        {
            image: Product?.photo?.image?.publicUrlTransformed,
            text: Product?.photo?.altText
        },
        {
            image: Product?.photo2?.image?.publicUrlTransformed,
            text: Product?.photo2?.altText
        },
        {
            image: Product?.photo3?.image?.publicUrlTransformed,
            text: Product?.photo3?.altText
        },
        {
            image: Product?.photo4?.image?.publicUrlTransformed,
            text: Product?.photo4?.altText
        },
    ];
    
    return (
        <SingleItemStyles>
            <Head>
                <title>Stax Of Stories - {Product.name}</title>
            </Head>
            <div className="item-details">

                <SliderImage 
                    data={dataImage} 
                    width="500px" 
                    showDescription={false} 
                    direction="right" 
                />

                <div className="details">
                    <h2>{Product.name}</h2>
                    <p>{Product.description}</p>
                    <PriceTag>{formatMoney(Product.price)}</PriceTag>
                    <ItemStyles>
                        <p>{Product?.ageRange} years</p>
                        <p>By {Product?.author}</p>
                        {
                            Product.inStock < 6 && Product.inStock > 0 ? <p className="font-weight-bold" style={{color: Product.inStock < 6 && Product.inStock > 0 ? "red" : "#8dc63f"}}>Low stock</p> : '' 
                        }
                        {
                            Product.inStock < 1 ? <p className="font-weight-bold" style={{color: Product.inStock < 1 ? "red" : "#8dc63f"}}>Back in stock soon!</p> : '' 
                        }

                        {!user && ( 
                            <>
                                <div className="buttonList">
                                    <a href="/signup" prefetch="true"><button className="cart-btn">Sign In/Buy</button></a>
                                </div>
                            </>
                        )}

                        {user?.role === null && ( 
                            <>
                                {
                                    Product?.inStock > 1 ? 
                                    <div className="buttonList">
                                        <AddToCart id={Product.id} />
                                    </div> : '' 
                                }
                            </>
                        )}

                        {user?.role?.canManageProducts === true && ( 
                            <>
                                {
                                    Product?.inStock > 1 ? 
                                    <div className="buttonList">
                                        <AddToCart id={Product.id} />
                                    </div> : '' 
                                }
                                <div className="buttonList">
                                    <Link href={{
                                        pathname: '/update',
                                        query: {
                                            id: Product.id,
                                        },
                                    }}>
                                    <a><button className="btn btn-success">Edit</button></a>
                                    </Link>
                                    <a><DeleteProduct id={Product.id}>Delete Product</DeleteProduct></a>
                                </div>
                            </>
                        )}
                    </ItemStyles>
                </div>
            </div>

            <h2>Write a review</h2>
            <Center>
                <CreateRating product={Product} />
                <br /> 
                
                {Product.review.length < 1 && (
                    <p>Be the first to write a review</p>
                )}

                {Product.review.length > 0 && (
                    <ItemRatingsList>
                        <div className="list">
                            {listItems.map((review, n) => (
                                <div key={n}>
                                    <Reviews review={review} />
                                </div>
                            ))}
                        </div>
                        {isFetching && "Fetching more reviews..."}
                    </ItemRatingsList>
