import Form from './styles/Form';
import React, { useState } from 'react';
import emailjs from 'emailjs-com'; 

export default function ContactRequest() {

    function handleSubmit(e) {
        e.preventDefault();

        emailjs.sendForm('gmail', 'contact_pj4avag', Form.current, 'user_uecjPyjaRPSXr3Lncpkcz')
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        Form.current.reset()
    };

    return (
        <Form onSubmit={handleSubmit}>
            <fieldset>
				<h2>Contact Us</h2>
				<br />
				<h4>Leave a message for us right here and weâ€™ll get back to you as soon as we can!</h4>
				<br />
                <label htmlFor="name">
					Name
                    <input 
                        type="name" 
                        name="name" 
                        required
                        placeholder="Your Name" 
                        autoComplete="name"
                        value={text}
                        onChange={handleClick}
                    />
                </label>
                <label htmlFor="email">
					Email
                    <input 
                        type="email" 
                        name="email" 
                        required
                        placeholder="Your Email Address" 
                        autoComplete="email"
                        value={text}
                        onChange={handleClick}
                    />
                </label>
                <label htmlFor="subject">
					Subject
                    <input 
                        type="text" 
                        name="subject" 
                        required
                        placeholder="Subject" 
                        autoComplete="subject"
                        value={text}
                        onChange={handleClick}
                    />
                </label>
                <label htmlFor="message">
                    Message
                    <textarea 
                        type="text" 
                        name="message" 
                        placeholder="Message" 
                        value={text}
                        onChange={handleClick}
                        required
                    />
                </label>
                <button type="submit" onClick={handleSubmit}>Send Message</button>
                <br/>
            </fieldset>
        </Form>
    );
}
