import Form from './styles/Form';
import React, { useState } from 'react';
import axios from 'axios';

export default function ContactRequest() {

    const [sent, setSent] = useState(false)
    const [text, setText] = useState("")
    
    const handleSend = async(e) => {
        e.preventDefault();
        setSent(true)
        try {
            await axios.post("http://localhost:3000/send_mail", {
                text
            })
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <Form method="POST" onSubmit={handleSend}>
            {!sent ? (
            <fieldset>
				<h2>Contact Us</h2>
				<br />
				<h4>Leave a message for us right here and weâ€™ll get back to you as soon as we can!</h4>
				<br />
                <label htmlFor="text">
					Email
                    <input 
                        type="text" 
                        name="text" 
                        required
                        placeholder="Your Email Address" 
                        autoComplete="text"
                        value={text}
                        onChange={(e) => setText(e.target.value)}
                    />
                </label>
                <button type="submit">Send Message</button>
                <br/>
            </fieldset>
            ): (
                <h3>Success! Thank you for your message!</h3>
            )}
        </Form>
    );
}
