import React, { useState, useEffect, useRef } from "react";
import styled from 'styled-components';
import gql from "graphql-tag";
import { useQuery } from '@apollo/client';
import DisplayError from "./ErrorMessage";
import Head from 'next/head';
import Link from 'next/link';
import SingleItemStyles from './styles/SingleItemStyles';
import DeleteProduct from './DeleteProduct';
import PriceTag from './styles/PriceTag';
import formatMoney from '../lib/formatMoney';
import SliderImage from 'react-zoom-slider';
import { useUser } from './User';
import AddToCart from './AddToCart';
import ItemStyles from './styles/ItemStyles';
import CreateRating from './CreateRating';
import Reviews from './Reviews';
import SomeProduct from "./SomeProduct";

const Center = styled.div`
	text-align: center;
`;

const ItemsList = styled.div`
	display: grid;
	grid-template-columns: auto auto auto auto;
	grid-gap: 60px;
	max-width: ${props => props.theme.maxWidth};
	margin: 0 auto;
	@media (max-width: 830px) {
		grid-template-columns: 1fr 1fr;
		grid-gap: 0%;
		a {
    		font-size: 1.2rem;
		}
	}
	@media (max-width: 500px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;
	}
`;

const ItemRatingsList = styled.div`
	display: grid;
	grid-template-columns: 1fr;
	grid-gap: 60px;
	max-width: ${props => props.theme.maxWidth};
	margin: 0 auto;
	text-align: left;
    margin-bottom: 50px;

	p{
		font-size: 1rem;
	}

	@media (max-width: 830px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;

		p {
    		font-size: 0.95rem;
		}
		
		a {
    		font-size: 1.2rem;
		}
	}
	@media (max-width: 500px) {
		grid-template-columns: 1fr;
		grid-gap: 0%;
	}
`;


const SINGLE_ITEM_QUERY = gql`
    query SINGLE_ITEM_QUERY ($id: ID!, $skip: Int = 0, $first: Int = 20){
        Product(where: { id: $id }) {
            name
            price
            description
            inStock
            ageRange
            author
            id
            photo {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo2 {
                altText
                image {
                    publicUrlTransformed
                }
            }
            photo3 {
                altText
                image {
                    publicUrlTransformed
                }
            }

            photo4 {
                altText
                image {
                    publicUrlTransformed
                }
            }

            review (first: $first, skip: $skip, sortBy: stars_DESC) {
                id
                reviewTitle
                comment
                stars
            }
            
        }
    }
`;

export default function SingleProduct({ id }) {
    const user = useUser();

    const { data, loading, error } = useQuery
        (SINGLE_ITEM_QUERY, {
            variables: {
                id,
            },
        });

    if (loading) return <p>Loading...</p>;
    if (error) return <DisplayError error={error} />;

    const { Product } = data;

    const dataImage = [
        {
            image: Product?.photo?.image?.publicUrlTransformed,
            text: Product?.photo?.altText
        },
        {
            image: Product?.photo2?.image?.publicUrlTransformed,
            text: Product?.photo2?.altText
        },
        {
            image: Product?.photo3?.image?.publicUrlTransformed,
            text: Product?.photo3?.altText
        },
        {
            image: Product?.photo4?.image?.publicUrlTransformed,
            text: Product?.photo4?.altText
        },
    ];

    // initialize list of posts
    const [postList, setPostList] = useState({
        list: Product.review
    }); 

    // tracking on which page we currently are
    const [page, setPage] = useState(1);
    // add loader refrence 
    const loader = useRef(null);

    useEffect(() => {
         var options = {
            root: null,
            rootMargin: "20px",
            threshold: 1.0
         };
        // initialize IntersectionObserver
        // and attaching to Load More div
         const observer = new IntersectionObserver(handleObserver, options);
         if (loader.current) {
            observer.observe(loader.current)
         }

    }, []);


    useEffect(() => {
        // here we simulate adding new posts to List
        const newList = postList.list.concat([1,1,1,1]);
        setPostList({
            list: newList
        })
    }, [page])

    // here we handle what happens when user scrolls to Load More div
   // in this case we just update page variable
    const handleObserver = (entities) => {
        const target = entities[0];
        if (target.isIntersecting) {   
            setPage((page) => page + 1)
        }
    }

    console.log(postList)
    
    return (
        <SingleItemStyles>
            <Head>
                <title>Stax Of Stories - {Product.name}</title>
            </Head>
            <div className="item-details">

                <SliderImage 
                    data={dataImage} 
                    width="500px" 
                    showDescription={false} 
                    direction="right" 
                />

                <div className="details">
                    <h2>{Product.name}</h2>
                    <p>{Product.description}</p>
                    <PriceTag>{formatMoney(Product.price)}</PriceTag>
                    <ItemStyles>
                        <p>{Product?.ageRange} years</p>
                        <p>By {Product?.author}</p>
                        {
                            Product.inStock < 6 && Product.inStock > 0 ? <p className="font-weight-bold" style={{color: Product.inStock < 6 && Product.inStock > 0 ? "red" : "#8dc63f"}}>Low stock</p> : '' 
                        }
                        {
                            Product.inStock < 1 ? <p className="font-weight-bold" style={{color: Product.inStock < 1 ? "red" : "#8dc63f"}}>Back in stock soon!</p> : '' 
                        }

                        {!user && ( 
                            <>
                                <div className="buttonList">
                                    <a href="/signup" prefetch="true"><button className="cart-btn">Sign In/Buy</button></a>
                                </div>
                            </>
                        )}

                        {user?.role === null && ( 
                            <>
                                {
                                    Product?.inStock > 1 ? 
                                    <div className="buttonList">
                                        <AddToCart id={Product.id} />
                                    </div> : '' 
                                }
                            </>
                        )}

                        {user?.role?.canManageProducts === true && ( 
                            <>
                                {
                                    Product?.inStock > 1 ? 
                                    <div className="buttonList">
                                        <AddToCart id={Product.id} />
                                    </div> : '' 
                                }
                                <div className="buttonList">
                                    <Link href={{
                                        pathname: '/update',
                                        query: {
                                            id: Product.id,
                                        },
                                    }}>
                                    <a><button className="btn btn-success">Edit</button></a>
                                    </Link>
                                    <a><DeleteProduct id={Product.id}>Delete Product</DeleteProduct></a>
                                </div>
                            </>
                        )}
                    </ItemStyles>
                </div>
            </div>

            <Center>
                <h2>Write a review</h2>
                <CreateRating product={Product} />
                <br /> 
                
                {Product.review.length < 1 && (
                    <p>Be the first to write a review</p>
                )}

                {Product.review.length > 0 && (
                    <ItemRatingsList>
                        <div className="list">
                            {postList.list.map(review => (
                                <div key={review.id}>
                                    <Reviews review={review} />
                                </div>
                            ))}
                            <div className="loading" ref={loader}><h2>Load More</h2></div>
                        </div>
                    </ItemRatingsList>
                )}

                <br />
                <hr />

                <h2>Others you may like</h2>

                <SomeProduct />
            </Center>
        </SingleItemStyles>
    );
}
