import Head from 'next/head';
import { useQuery } from '@apollo/client';
import Link from 'next/link';
import gql from 'graphql-tag';
import PaginationStyles from './styles/PaginationStyles';
import DisplayError from '../components/ErrorMessage'
import { perPage } from '../config';

export const PAGINATION_QUERY = gql `
    query PAGINATION_QUERY {
        _allProductsMeta {
            count
        }
    }
`;

export default function Pagination({ page, product }) {
    const { error, loading, data } = useQuery(PAGINATION_QUERY);
    if(loading) return 'Loading...';
    if(error) return <DisplayError error={error} />;
    const { count } = data._allProductsMeta;
    const pageCount = Math.ceil(count / perPage);

    return (
        <PaginationStyles>
            <Head>
                <title>Stax Of Stories - Page {page} of {pageCount}</title>
            </Head>
            {page !== 1 && <Link href={`/products/${productId}/${page - 1}`}>Prev</Link>}
            <p>Page {page} of {pageCount}</p>
            <p>{count} Items Total</p>
            {page !== pageCount && <Link href={`/products/${productId}/${page + 1}`}>Next</Link>}
        </PaginationStyles>
    );
    
}
