import styled from 'styled-components';
import CartStyles from './styles/CartStyles';
import CartTitle from './styles/CartTitle';
import { useUser } from './User';
import { useCart } from '../lib/cartState';
import calcTotalPrice from '../lib/calcTotalPrice';
import CloseButton from './styles/CloseButton';
import RemoveFromCart from './RemoveFromCart';
import formatMoney from '../lib/formatMoney';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
    faTimes,
} from '@fortawesome/free-solid-svg-icons';

const CartItemStyles = styled.li`
	padding: 1rem 0;
	border-bottom: 0.5px solid ${props => props.theme.grey};
	display: grid;
	align-items: center;
	grid-template-columns: auto 1fr auto;
	img {
		margin-right: 10px;
	}
	h3,
	p {
		margin: 0;
	}

	@media (max-width: 1214px) {
    	grid-template-columns: 1fr;
  	}
`;

const Overlay = styled.div `
  &:before {
    content: "'hi'";
    display: block;
    background: rgba(0, 0, 0, 0.6);
    position: fixed;
    top: 0;
    left: -860px;
    right: 0;
    bottom: 0;
    z-index: 11;
  }
`;

function CartItem({ cartItem }) {
    const { product } = cartItem;
    if (!product) return null;
    return (
        <CartItemStyles>
          <img
            width="100"
            src={product.photo.image.publicUrlTransformed}
            alt={product.name}
          />
          <div>
            <h3>{product.name}</h3>
            <p>
              {formatMoney(product.price * cartItem.quantity)}-
              <em>
                {cartItem.quantity} &times; {formatMoney(product.price)} each
              </em>
            </p>
          </div>
          <RemoveFromCart id={cartItem.id} />
        </CartItemStyles>
    );
}
  
export default function Cart() {
    const me = useUser();
    const { cartOpen, closeCart, cartOverlay } = useCart();
    if (!me) return null;
    return (
      <CartStyles open={cartOpen}>
        <header>
          <CartTitle>{me.email}'s Cart</CartTitle>
          <CloseButton onClick={closeCart}><FontAwesomeIcon icon={faTimes} /></CloseButton>
        </header>
        <ul>
          {me.cart.map((cartItem) => (
            <CartItem key={cartItem.id} cartItem={cartItem} />
          ))}
        </ul>
        <footer>
          <p>{formatMoney(calcTotalPrice(me.cart))}</p>
        </footer>
      </CartStyles>
    );
}
