import { useState } from "react";
import { useMutation, useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import useForm from '../lib/useForm';
import styled from 'styled-components';
import { ALL_PRODUCTS_QUERY } from './Products';
import Form from './styles/Form';
import DisplayError from './ErrorMessage';
import Router from 'next/router';
import { StarsRating } from "stars-rating-react-hooks";

const CREATE_PRODUCT_REVIEW_MUTATION = gql`
    mutation CREATE_PRODUCT_REVIEW_MUTATION (
        $reviewTitle: String! 
        $comment: String!
        $stars: Int!
        $review: ProductRelateToOneInput!
    ){
        createProductReview ( 
            data: {
                reviewTitle: $reviewTitle
                comment: $comment
                stars: $stars 
                review: $review
            }
        ) {
            id
            reviewTitle
            comment
            stars
            review {
                id
                name
                author
            }
        }
    }
`;

const StyledRatingWrapper = styled.div`
label .dv-star-rating .dv-star-rating-star {
    display: inline-block;
    padding: 5px;
    background: transparent;
    float: left;
}
`;

export default function createShippingMethod ({product}) {
    const [selecting, setSelecting] = useState(null);

    const { inputs, handleChange, clearForm } = useForm({
        reviewTitle: '',
        comment: '',
        stars: 1,
        review: {
            connect: { id: product.id }
        },
    });

    const [createProductReview, { data, error, loading }] = useMutation(
        CREATE_PRODUCT_REVIEW_MUTATION, 
        {
            variables: inputs,
            refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
        }
    );
    
    return (
        <select
            name="transcode_profile"
            id="transcode_profile"
            onChange={(e) => {
                setShippingMethod(e.target.value);
            }}
            value={shippingMethod.type}
            >
                <option value="Select Shipping Method">Select Shipping Method</option>
                {data?.allShippings.map(shippingMethod => <option value={shippingMethod.id}>{shippingMethod.type} - {shippingMethod.amount}</option>)}
        </select>
    );
}
