import React, { useRef, useState, useContext } from 'react';
import styled from 'styled-components';
import {
    CardElement,
    Elements,
    useElements,
    useStripe,
  } from '@stripe/react-stripe-js';
import { loadStripe } from '@stripe/stripe-js';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
    faChevronRight,
} from '@fortawesome/free-solid-svg-icons';
import nProgress from 'nprogress';
import gql from 'graphql-tag';
import { useMutation, useQuery } from '@apollo/client';
import { CURRENT_USER_QUERY, useUser } from './User';
import { useRouter } from 'next/dist/client/router';
import { useCart } from '../lib/cartState';
import { useCheckoutMethod } from '../lib/checkoutState';
import emailjs from 'emailjs-com';


const CheckoutFormStyles = styled.form`
    box-shadow: 0 1px 2px 2px rgba(0, 0, 0, 0.04);
    border: 1px solid rgba(0, 0, 0, 0.06);
    border-radius: 5px;
    padding: 1rem;
    grid-gap: 1rem;
    height: 398px;
    overflow-y: scroll;
    
    input {
        font-size: 15px;
        margin-right: 5px;
        width: 100%;
    }

    select {
        font-size: 15px !important;
        margin-right: 5px;
        width: 100%;
        max-width: none !important;
        height: 37px;

        option {
            font-size: 15px;
        }
    }


    .side-by-side {
        display: grid;
        grid-template-columns: 1fr 1fr;
        grid-gap: 22px;
    }
`;

const SHIPPING_QUERY = gql`
    query SHIPPING_QUERY {
        allShippings {
            id
            type
            amount
            description
        }
    }
`;

const CREATE_ORDER_MUTATION = gql`
    mutation CREATE_ORDER_MUTATION ($token: String!) {
        checkout(token: $token) {
            id
            charge
            total
            items {
                id
                name
            }
        }
    }
`;

const stripeLib = loadStripe(process.env.NEXT_PUBLIC_STRIPE_KEY);

function CheckoutForm() {

    const form = useRef();
    const [status, setStatus] = useState(undefined);

    const user = useUser();

    const [error, setError] = useState();
    const [loading, setLoading] = useState(false);
    const stripe = useStripe();
    const router = useRouter();
    const elements = useElements();
    const [name, setName] = useState('');
    const [email, setEmail] = useState(user.email);
    const [postal, setPostal] = useState('');
    const [city, setCity] = useState('');
    const [country, setCountry] = useState('');
    const [line1, setLine1] = useState('');
    const [line2, setLine2] = useState('');
    const [state, setState] = useState('');

    const [shipEmail, setShipEmail] = useState(user.email);

    const { closeCart } = useCart();

    const { 
        // Shipping States
        shipName, 
        shipPostal,
        shipCity,
        shipCountry,
        shipLine1,
        shipLine2,
        shipState,
        shippingMethod,
        paymentInfo,

        //Shipping Set States
        onChangeShipName,
        onChangeShipPostal,
        onChangeShipCity,
        onChangeShipCountry,
        onChangeShipLine1,
        onChangeShipLine2,
        onChangeShipState,
        onChangeShippingMethod,
        setPaymentInfo
    } = useCheckoutMethod();

    console.log(shippingMethod, shipName, shipEmail, 'test');

    // console.log(setShipName);

    const [checkout, {error: graphQLError}] = useMutation(
        CREATE_ORDER_MUTATION,
        {
            refetchQueries: [{ query: CURRENT_USER_QUERY }],
        }
    );

    const { data } = useQuery(SHIPPING_QUERY);

    console.log(data?.allShippings);

    async function handleSubmit(e) {
        //1. Stop the form from submitting and turn the loader one
        e.preventDefault();

        emailjs.sendForm('service_ipz8izo', 'order_6vr15fv', form.current, 'user_uecjPyjaRPSXr3Lncpkcz')
        .then((result) => {
            console.log(result.text);
            setStatus({type: 'result'});
        }, (error) => {
            console.log(error.text);
            setStatus({type: 'error'})
        });

        setLoading(true);
        console.log('We gotta do some work...');
        //2. Start the page transition
        nProgress.start();
        //3. Create the payment method via stripe
        const { error, paymentMethod } = await stripe.createPaymentMethod({
            type: 'card',
            card: elements.getElement(CardElement),
            billing_details: {
                email,
                name,
                address: {
                    city: city,
                    country: country,
                    line1: line1,
                    line2: line2,
                    postal_code: postal,
                    state: state
                }
            }
        });

        //4. Handle any errors from stripe (Token comes back here if successful)
        if (error) {
            setError(error);
            nProgress.done();
            return; //stops the checkout from happening
        }
        //5. Send the token from step 3 to our keystone server, via a custom mutation!
        const order = await checkout({
            variables: {
                token: paymentMethod.id,
            },
        });
        console.log(`Finished with the order!!`);

        setPaymentInfo(order.data.checkout.total);

        console.log(paymentInfo, "test");

        // console.log(option);

        //6. Change the page to vie the order
        router.push({
            pathname: `/order/[id]`,
            query: { id: order.data.checkout.id },
        });
        
        //7. Close the cart 
        closeCart()

        //8. Turn the loader off
        setLoading(false);
        nProgress.done();
    }

    return (
        <CheckoutFormStyles ref={form} method="POST" onSubmit={handleSubmit}>
            <fieldset>
                <div className="side-by-side">
                    <div>
                         <input
                            id="email"
                            type="hidden"
                            required
                            placeholder="Email"
                            value={paymentInfo}
                        />
                        <input
                            id="email"
                            type="hidden"
                            required
                            placeholder="Email"
                            value={email}
                            onChange={(e) => {
                            setEmail(e.target.value);
                            }}
                        />
                        Billing Details
                        <input
                            id="name"
                            required
                            placeholder="Full Name"
                            value={name}
                            onChange={(e) => {
                            setName(e.target.value);
                            }}
                        />
                        <input
                            id="line1"
                            required
                            placeholder="Address Line1"
                            value={line1}
                            onChange={(e) => {
                            setLine1(e.target.value);
                            }}
                        />
                        <input
                            id="line2"
                            placeholder="Address Line2"
                            value={line2}
                            onChange={(e) => {
                            setLine2(e.target.value);
                            }}
                        />
                        <input
                            id="city"
                            required
                            placeholder="City"
                            value={city}
                            onChange={(e) => {
                            setCity(e.target.value);
                            }}
                        />
                        <input
                            id="country"
                            required
                            placeholder="Country"
                            value={country}
                            onChange={(e) => {
                            setCountry(e.target.value);
                            }}
                        />
                        <input
                            id="state"
                            required
                            placeholder="State/County"
                            value={state}
                            onChange={(e) => {
                            setState(e.target.value);
                            }}
                        />
                        <input
                            id="postal"
                            required
                            placeholder="Post Code"
                            value={postal}
                            onChange={(e) => {
                            setPostal(e.target.value);
                            }}
                        />
                    </div>

                    <div>
                        <input
                            id="email"
                            type="hidden"
                            required
                            placeholder="Email"
                            value={shipEmail}
                            onChange={(e) => {
                            setShipEmail(e.target.value);
                            }}
                        />
                        Shipping Details
                        <input
                            id="shipName"
                            required
                            placeholder="Full Name"
                            value={shipName}
                            onChange={onChangeShipName}
                        />
                        <input
                            id="shipLine1"
                            required
                            placeholder="Address Line1"
                            value={shipLine1}
                            onChange={onChangeShipLine1}
                        />
                        <input
                            id="shipLine2"
                            placeholder="Address Line2"
                            value={shipLine2}
                            onChange={onChangeShipLine2}
                        />
                        <input
                            id="shipCity"
                            required
                            placeholder="City"
                            value={shipCity}
                            onChange={onChangeShipCity}
                        />
                        <input
                            id="shipCountry"
                            required
                            placeholder="Country"
                            value={shipCountry}
                            onChange={onChangeShipCountry}
                        />
                        <input
                            id="shipState"
                            required
                            placeholder="State/County"
                            value={shipState}
                            onChange={onChangeShipState}
                        />
                        <input
                            id="shipPostal"
                            required
                            placeholder="Post Code"
                            value={shipPostal}
                            onChange={onChangeShipPostal}
                        />
                    </div>
                </div>

                <select
                    name="selectPaymentMethod"
                    id="selectPaymentMethod"
                    onChange={onChangeShippingMethod}
                    value={shippingMethod}
                    >
                        <option value="Select Shipping Method">Select Shipping Method</option>
                        {data?.allShippings.map(shippingMethod => <option value={`${shippingMethod.type} - ${shippingMethod.amount}`}>{shippingMethod.type} - {shippingMethod.amount}</option>)}
                </select>

                {error && <p style={{ fontSize: 12 }}>{error.message}</p>}
                {graphQLError && <p style={{ fontSize: 12 }}>{graphQLError.message}</p>}
                <CardElement />
                <button id="buyIt" className="btn btn-success checkout" type="submit">Buy Now <FontAwesomeIcon icon={faChevronRight} /></button>
            </fieldset>
        </CheckoutFormStyles>
    );
}

function Checkout() {
    return (
        <Elements stripe={stripeLib}>
            <CheckoutForm />
        </Elements>
    )
}

export {Checkout};
