// import NavLink from './Link'
import React from 'react';
import { useUser, CURRENT_USER_QUERY } from './User';
import { useQuery } from '@apollo/client';
import { format } from 'date-fns';
import Head from 'next/head';
import gql from 'graphql-tag';
import formatMoney from '../lib/formatMoney';
import Error from './ErrorMessage';
import styled from 'styled-components';
import OrderStyles from './styles/OrderStyles';
import SubHeader from '../components/SubHeader';
// import DeleteUser from './DeleteUser'; 

const SINGLE_ORDER_QUERY = gql`
	query SINGLE_ORDER_QUERY($id: ID!) {
		order(id: $id) {
			id
			charge
			total
			createdAt
			user {
				id
			}
			items {
				id
				title
				description
				price
				image
				quantity
			}
		}
	}
`;

const StyledLinked = styled.div`

  	a {
    	color: ${props => props.theme.navyBlue};
  	}

  	a:hover {
    	color: ${props => props.theme.navyBlue};
    	text-decoration: none;
  	}

`;

export default function Account({ id }) {
	const user = useUser();

	const { data, error, loading } = useQuery(CURRENT_USER_QUERY, {
        variables: {
            id
        },
    });

	console.log(data)

	if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;
		
	return (
		<StyledLinked>
			{/* <Query query={CURRENT_USER_QUERY} variables={{ id: this.props.id }}> */}
				{/* {({ data, error, loading }) => {
					if (error) return <Error error={error} />;
					if (loading) return <p>Loading</p>;
					const user = data.me;
					const me = data ? data.me : null; */}
						
							<div>
								<SubHeader />
								<OrderStyles data-test="user">
									<p>
										<span>User ID:</span> 
										<span>{data.User.id}</span>
									</p>
									<p>
										<span>Email:</span>
										<span>{data.User.email}</span>
									</p>

									<p>
										<span>Account Action:</span>
										<span>
											<a><DeleteUser id={data.User.id}>Delete Account</DeleteUser></a>
										</span>
									</p>
										{/* {me && (
											<>
												<p>
													<span>Orders:</span>
													<span>
														<NavLink activeClassName='active' prefetch="true" href="/orders">
															<u><a href="">Purchased Orders</a></u>
														</NavLink>
														
													</span>
												</p>

												{me.permissions.includes("ADMIN") && (
												<>
													<p>
														<span>All User Permissions:</span>
														<span>
															<NavLink activeClassName='active' prefetch="true" href="/permissions">
																<u><a href="">Permissions</a></u>
															</NavLink>
														</span>
													</p>
												</>
												)}

												<p>
													<span>Delivery Method:</span>
													<span>
														<NavLink activeClassName='active' prefetch="true" href="/createDeliveryMethod">
															<a>Create Delivery</a>
														</NavLink>
													</span>
												</p>
											</>
										)} */}
									
								</OrderStyles>
							</div>
						
				{/* }} */}
				
			{/* </Query> */}
		</StyledLinked>
	);
}
