import { useQuery } from '@apollo/client';
import { useUser } from './User';
import ReviewStyles from './styles/ReviewStyles';
import Review from './Review';
import gql from "graphql-tag";
import { perPage } from '../config';

export const ALL_PRODUCT_REVIEWS_QUERY = gql`
    query ALL_PRODUCT_REVIEWS_QUERY($skip: Int = 0, $first: Int) {
        Product(where: { id: $id }, first: $first, skip: $skip){
            id
            review (first: $first, skip: $skip, sortBy: stars_DESC) {
                id
                reviewTitle
                comment
                stars
            }
        }
    }
`;

const [getReviews, { data, error, loading }] = useQuery(
    ALL_PRODUCT_REVIEWS_QUERY, 
    {
        variables: inputs,
        refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
    }
);

export default function Reviews({ product, review, page }) {

    console.log(product.review)

    const res = getReviews({
        variables: {
            skip: page * perPage - perPage,
            first: perPage,
        },
    });

    console.log(res);

    console.log( data, error, loading )
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;

    return (
        <ReviewStyles>
            {product.review.map((review) => (
                <Review key={review.id} review={review} page={page} />
            ))}
        </ReviewStyles>
    );
}
