import Link from 'next/link';
import StyledCarousel from './styles/CarouselStyles';
import { ScrollTo } from "react-scroll-to"
import React, { useState } from 'react';
// import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
// import { 
//     faArrowDown,
// } from '@fortawesome/free-solid-svg-icons';

// import { 
//     faFacebook,
//     faTwitter,
//     faInstagram
// } from '@fortawesome/free-brands-svg-icons';

const data = [
	{
	  id: "1",
	  key: "1",
	  img: "/static/fake-me2.png"
	},
	{
	  id: "2",
	  key: "2",
	  img: "/static/fake-me.png"
	},
]

export default function HomeCarousel() {
  const [toggle, setToggle] = useState();
  const [visible, setVisible] = useState(true);

//   const handleClick = () => {
// 	setChangeImage(changeImage);
// 	console.log('test click')
//   }

  return (
    <StyledCarousel>
		<div>
			{data.map(({ key, img }) => {
				<div id="carouselExampleIndicators" className="carousel slide" data-ride="carousel">
					<ol className="carousel-indicators d-none">
					<li data-target="#carouselExampleIndicators" data-slide-to="0" className="active"></li>
					<li data-target="#carouselExampleIndicators" data-slide-to="1"></li>
					<li data-target="#carouselExampleIndicators" data-slide-to="2"></li>
					</ol>
					{/*<div className="social-button-main">
					<div className="col-xs-12 col-sm-12 col-md-12">
						<ul className="list-unstyled list-inline social text-center">
						<li className="list-inline-item"><a href="https://www.facebook.com/staxofstories/" target="_blank"><FontAwesomeIcon icon={faFacebook} /></a></li>
						<li className="list-inline-item"><a href="#"><FontAwesomeIcon icon={faTwitter} /></a></li>
						<li className="list-inline-item"><a href="https://www.instagram.com/staxofstories/" target="_blank"><FontAwesomeIcon icon={faInstagram} /></a></li>
						</ul> 
					</div>
					</div>*/} 
					<div className="carousel-inner" role="listbox">
					<div className="carousel-item active">
						{/* <img src="/static/landing-pic.jpeg" /> */}
						<div className="center-carousel text-center">
							<div className="col-xs-12 col-md-6 col-lg-6">
								<img 
									className="stax-logo" 
									onClick={() => {
										setToggle(key);
										setVisible(false);
									}}
									src={visible && key === "1" ? "/static/fake-me2.png" : null} 
								/> 
							</div>
							<div className="col-xs-12 col-md-6 col-lg-6 my-5 carousel-caption heading d-md-block"> 
								<div className="bubble">
									<h2 className="display-4">Hi, I am Michael Sokan</h2>
									<h3 className="display-5">Pleased to meet you. Welcome to my world of design and code</h3>
								</div>
								<small>Front End Developer - UX/UI Designer</small>
								<br />
								<ScrollTo>
									{({ scroll }) => (
									<a onClick={() => scroll({ x: 20, y: 830, smooth: true })}><button className="btn btn-outline-light rounded-corner">See Work</button></a>
									)}
								</ScrollTo>
							</div>
						</div>
					</div>
					</div>
					<a className="carousel-control-prev d-none" href="#carouselExampleIndicators" role="button" data-slide="prev">
					<span className="carousel-control-prev-icon" aria-hidden="true"></span>
					<span className="sr-only">Previous</span>
					</a>
					<a className="carousel-control-next d-none" href="#carouselExampleIndicators" role="button" data-slide="next">
					<span className="carousel-control-next-icon" aria-hidden="true"></span>
					<span className="sr-only">Next</span>
					</a>
				</div>
			})}
	  </div>
	  </StyledCarousel>
  );
}
