import styled from 'styled-components';

const SingleItemStyles = styled.div`
	.item-details{
		margin: 2rem auto;
		display: block;
		grid-auto-columns: 1fr;
		grid-auto-flow: column;
		min-height: 800px;
		img{
			width: 100%;
			height: auto;
    		object-fit: contain;
			box-shadow: 0 1rem 3rem rgba(0,0,0,.175)!important;
    		border-radius: 20px;
    	// 		max-width: 1300%;
		//   	transition: transform 0.3s ease-in-out;
		
			@media (max-width: 768px) {
      			height: auto;
    		}
		}
	 	
	 	// img:hover {
		//   transform: scale(1.2);
		// }



		.carousel-item img[src=""] {
		  	display:none;

		}



		@media (max-width: 768px) {
      		grid-auto-flow: unset;
    	}
	}

	.carousel-item {
    	height: auto;
    }

    @media (max-width: 500px) {
		.carousel img {
		    width: 220%;
		    position: absolute;
		    left: 0;
		}

		.carousel-item {
		    height: auto;
		}
	}

	.nav-link {
    	display: block;
    	padding: 0;
    	font-size: 20px;
	}

	.details{
		margin: 10rem 0rem 0rem;
		font-size: 2rem;
		h2{
			color: ${props => props.theme.black};
			font-weight: bold;
		}
		p{
			font-size: 1.1rem;
		}

		@media (max-width: 768px) {
      		text-align: center;
      		margin: 0;
    	}

    	.dv-star-rating {
	    	display: inline !important;
	    	float: left;

	    	@media (max-width: 768px) {
	      		float: none;
	    	}

	    	.dv-star-rating-star {
	    		@media (max-width: 768px) {
	      			float: none !important;
	    		}
	  		}
	  	}

	  	.star-result {
	  		margin-left: 10px;
	  	}
	}

	button.react-slider__btnNext {
		margin: 20px;
		border-radius: 0px !important;
	}

	button.react-slider__btnPrev {
		margin: 20px;
		border-radius: 0px !important;
	}

	.react-slider{
		margin: 0 auto;
		width auto !important;
		max-width 100%;
		display: flex;

		@media (max-width: 768px) {
			width auto !important;
			max-width 700px;
			display: block;
	  	}
	}

	.react-slider__imgZoom {
		background-size: 2490px auto !important;
		height: 647px;
		display: none;

		@media (max-width: 768px) {
			display: none;
		}
	}

	.react-slider__picture::after {
		content: 'No Image...';
		position: absolute;
		width: 100%;
		height: 100%;
		top:0;
		left: 0;
		display: flex;
		align-items: center;
		justify-content: center;
		z-index: 1
	}

	.react-slider__ul {
		display: block;
	}
`;

export default SingleItemStyles;
