import { useState } from "react";
import { useMutation, useQuery } from '@apollo/client';
import gql from 'graphql-tag';
import useForm from '../lib/useForm';
import styled from 'styled-components';
import { ALL_PRODUCTS_QUERY } from './Products';
import Form from './styles/Form';
import DisplayError from './ErrorMessage';
import Router from 'next/router';
import { StarsRating } from "stars-rating-react-hooks";

const CREATE_RATING_MUTATION = gql`
    mutation CREATE_RATING_MUTATION (
        $ratingSubject: String! 
        $ratingImage: String!
        $ratingDescription: String!
        $ratingStar: Int!
        $itemId: ID!
    ){
      createRating (
            ratingSubject: $ratingSubject
            ratingImage: $ratingImage
            ratingDescription: $ratingDescription
            ratingStar: $ratingStar
            itemId: $itemId              
      ) {
        ratingSubject
        ratingDescription
        ratingStar
          item {
            id
          }
      }
    }
`;

const StyledRatingWrapper = styled.div`
label .dv-star-rating .dv-star-rating-star {
    display: inline-block;
    padding: 5px;
    background: transparent;
    float: left;
}
`;

export default function CreateRating ({id}) {
    const { inputs, handleChange, clearForm, resetForm } = useForm({
        ratingSubject: 'nice book',
        ratingDescription: '5-6',
        ratingStar: 'Jane',
        itemId: id
    });

    const [createRating, { data, error, loading }] = useMutation(
        CREATE_RATING_MUTATION, 
        {
            variables: inputs,
            refetchQueries: [{ query: ALL_PRODUCTS_QUERY }],
        }
    );

    const config = {
        totalStars: 5,
        initialSelectedValue: 4.5,
        renderFull: (
            <img
            alt="star"
            src="https://img.icons8.com/office/16/000000/star--v1.png"
            />
        ),
        renderEmpty: (
            <img alt="star" src="https://img.icons8.com/office/16/000000/star--v1.png" />
        ),
        renderHalf: (
            <img
            alt="star"
            src="https://img.icons8.com/office/16/000000/star-half.png"
            />
        )
    };
    
    const [selecting, setSelecting] = useState(null);

    console.log(createRating);

    console.log(inputs.ratingSubject);

    return (
        <Form 
            onSubmit={async (e) => {
                e.preventDefault();
                //Submit the inputfield to the backend
                const res = await createRating();
                clearForm();
                // change them to the single item page;
                window.location.reload();
            }}
        >
            <DisplayError error={error} />
            <fieldset disabled={loading} aria-busy={loading}>
                <StyledRatingWrapper>
                    <label htmlFor="name">
                        Rating Subject
                        <input 
                            type="text" 
                            id="ratingSubject" 
                            name="ratingSubject" 
                            placeholder="Review Title"
                            required
                            value={inputs.ratingSubject}
                            onChange={handleChange}
                        />
                    </label>
                    <label htmlFor="author">
                        Rating Description
                        <input 
                            type="text" 
                            id="ratingDescription" 
                            name="ratingDescription" 
                            placeholder="Rating Description"
                            required
                            value={inputs.ratingDescription}
                            onChange={handleChange}
                        />
                    </label>
                    <label htmlFor="price">
                        Rating Star
                        <input 
                            type="number" 
                            id="ratingStar" 
                            name="ratingStar" 
                            placeholder="Rating Star"
                            required
                            value={inputs.price}
                            onChange={handleChange}
                        />
                    </label>

                    <h3>{selecting?.selectingValue}</h3>
                    <div>
                        <StarsRating
                        onStarsRated={(value) => {
                            alert(`${value} stars rated`);
                        }}
                        onSelecting={(isSelecting, selectingValue) => {
                            setSelecting({ isSelecting, selectingValue });
                        }}
                        config={config}
                        />
                    </div>

                    <label htmlFor="ageRange">
                        Age Range
                        <input 
                            type="text" 
                            id="ageRange" 
                            name="ageRange" 
                            placeholder="Enter an Age Range"
                            required
                            value={inputs.ageRange}
                            onChange={handleChange}
                        />
                    </label>
                    <label htmlFor="description">
                        Description
                        <textarea 
                            id="description" 
                            name="description" 
                            rows="10"
                            placeholder="Enter A Description" 
                            required 
                            value={inputs.description}
                            onChange={handleChange}
                        />
                    </label>
                    <button type="submit">Submit</button>
                    <button type="button" onClick={clearForm}>Clear Form</button>
                    <button type="button" onClick={resetForm}>Reset Form</button>
                </StyledRatingWrapper>
            </fieldset>
        </Form>
    );
}
